/* eslint-disable jsx-a11y/alt-text */
import React from 'react';
import './style.scss';
import Button from '../../../../common/Button/Button';
import * as constant from '../../../../constant';

function getAuthorNames(authorids) {
	var authorNameArray = constant.mockedAuthorsList;
	var authorNames = '';
	for (const author of authorids){
		console.log(author);
	}
}

let items = constant.mockedCoursesList;
let itemList = [];
items.forEach((item, index) => {
	itemList.push(
		<div className='movie_card' id='bright'>
			<div className='info_section'>
				<div className='movie_header' style={{ float: 'left' }}>
					<h1>{item.title}</h1>
					<p className='type'>{item.description}</p>
				</div>

				<div className='course-meta' style={{ float: 'left', padding: '25px' }}>
					<dl className='row'>
						<dt className='col'>Author</dt>
						<dd className='col'>{item.authors[0]}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'>Duration</dt>
						<dd className='col'>{item.duration}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'>Created</dt>
						<dd className='col'>{item.creationDate}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'></dt>
						<dd className='col'>
							<Button buttonText='Show course' />
						</dd>
					</dl>
				</div>
			</div>
		</div>
	);
});

function CourseCard(props) {
	const { buttonText, onClick, ...others } = props;
	return <>{itemList}</>;
}

export default CourseCard;
