import React from 'react';
import CourseCard from './components/CourseCard/CourseCard';
import SearchBar from './components/SearchBar/SearchBar';
import Button from '../../common/Button/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'bootstrap/dist/css/bootstrap.min.css';

import * as constant from '../../constant';

let items = constant.mockedCoursesList;
let itemList = [];

items.forEach((item, index) => {
	itemList.push(
		<div className='movie_card' id='bright' key={index}>
			<div className='info_section'>
				<div className='movie_header' style={{ float: 'left' }}>
					<h1>{item.title}</h1>
					<p className='type'>{item.description}</p>
				</div>

				<div className='course-meta' style={{ float: 'left', padding: '25px' }}>
					<dl className='row'>
						<dt className='col'>Author</dt>
						<dd className='col'>{getAuthorNames(item.authors)}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'>Duration</dt>
						<dd className='col'>{item.duration}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'>Created</dt>
						<dd className='col'>{item.creationDate}</dd>
					</dl>
					<dl className='row'>
						<dt className='col'></dt>
						<dd className='col'>
							<Button buttonText='Show course' />
						</dd>
					</dl>
				</div>
			</div>
		</div>
	);
});

function Courses(props) {
	const { toggleCreateCourses, ...others } = props;
	return (
		<div>
			<Container fluid>
				<Row className='mt-5'>
					<Col>
						<SearchBar />
					</Col>
					<Col>
						<Button buttonText='Add new course' onClick={toggleCreateCourses} />
					</Col>
				</Row>
				<Row>
					<Col>
						<CourseCard />
					</Col>
				</Row>
			</Container>
		</div>
	);
}

export default Courses;
